        nleEvt.dispose();
      end if;
    end for;
  else
    // if there is only can''t happens we dispose this
    // non-local event
    if(empty ignored and not_empty ch)
      evt.dispose();
    end if;
  end if;
end for;
// now clean up all event ignores, these will only exist if the
