    relate parm to msv across R842;
  end if;
  parm.Name = parmName;
  if ( param.b4_expr4_id != GD::NULL_UNIQUE_ID() )
    // this is the last in the list, get first in list
    select any next_parm from instances of V_PAR 
         where selected.Value_ID == param.b4_expr4_id;
