',
	19,
	1,
	'// PE_PE navigation is present (isInGenericPackage).  Do not remove this comment.
select one sys related by self->S_SYS[R28];
unrelate self from sys across R28;
select many sses related by self->S_SS[R43];
for each ss in sses
