      provision.formalize(Id:srcInterface.Id, formalizeReq:false);
    elif (not_empty targetInterface and formalizeSource)
      // common rule 5
      requirement.formalize(Id:targetInterface.Id, formalizePro:false);
    end if;
  end if;
end if;
